function typedAction(type: string, payload?: any) {
  return { type, payload };
}

type Product = {
  id: number;
  title: string;
  price: number;
  image?: string;
};

type CartItem = {
  id: number;
  title: string;
  quantity: number;
  price: number;
  image?: string;
};

export type ProductState = {
  cart: CartItem[];
};

const initialState: ProductState = {
  cart: [],
};

export const addToCart = (product: Product) => {
  return typedAction("ADD_TO_CART", product);
};

export const removeFromCart = (product: Product) => {
  return typedAction("REMOVE_FROM_CART", product);
};

export const incrementQuantity = (product: Product) => {
  return typedAction("INCREMENT_QUANTITY", product);
};

export const decrementQuantity = (product: Product) => {
  return typedAction("DECREMENT_QUANTITY", product);
};

type ProductAction = ReturnType<typeof addToCart>;

export default function reducer(
  state = initialState,
  action: ProductAction
): ProductState {
  switch (action.type) {
    case "ADD_TO_CART":
      const itemInCart = state.cart.find(
        (item) => item.id === action.payload.id
      );
      if (itemInCart) {
        itemInCart.quantity++;
        return state;
      } else {
        return {
          ...state,
          cart: [...state.cart, { ...action.payload, quantity: 1 }],
        };
      }

    case "REMOVE_FROM_CART":
      return {
        ...state,
        cart: state.cart.filter((item) => item.id !== action.payload.id),
      };
    case "INCREMENT_QUANTITY":
      let item = state.cart.find((item) => item.id === action.payload.id);
      if (item) {
        item.quantity++;
        return { ...state };
      }
      return state;
    case "DECREMENT_QUANTITY":
      let item2 = state.cart.find((item) => item.id === action.payload.id);
      if (item2) {
        if (item2.quantity == 1) {
          item2.quantity = 1;
        } else {
          item2.quantity--;
        }
        console.log(item2);
        return { ...state };
      }
      return state;
    default:
      return state;
  }
}
