import React, { FormEvent, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { useDispatch } from "react-redux";
import Breadcrums from "../components/Breadcrumbs";
import { CatalogList } from "../utils/constants";
import ProductCard from "../components/ProductCard";
import { setWidthFilter } from "../redux/actions";

const CatalogSectionPage = () => {
  const params = useParams();
  const topic = CatalogList.filter((item) => item.slug === params.slug)[0];
  const dispatch = useDispatch();
  const [filterProducts, setFilterProducts] = useState(topic.section);
  const [selected, setSelected] = useState<String>();

  return (
    <>
      <Breadcrums title={topic.title} />
      <div className="container inner_content">
        <div className="row">
          <div className="col-12 col-sm-12 col-md-12 col-lg-3 col-xl-3">
            <ul className="leftmenu">
              {CatalogList.map((item) => (
                <li
                  className={item.slug === params.slug ? "active" : ""}
                  key={item.slug}
                >
                  <Link to={`/catalog/${item.slug}`}>{item.title}</Link>
                </li>
              ))}
            </ul>
            {topic.section &&
              Array.from(
                new Set(topic.section.map((item: any) => item.width))
              ).map((val) => (
                <div className="filter_row" key={val}>
                  {selected === val.toString()}
                  <input
                    type="radio"
                    id={val}
                    name="width"
                    value={val}
                    // checked={selected === val.toString() ? "checked" : ""}
                    onChange={
                      (e: React.ChangeEvent<HTMLInputElement>) => (
                        /*  filterProducts &&
                            filterProducts.filter(
                              (item) => item.width === e.currentTarget.value
                            )*/
                        //console.log(e.currentTarget.value)
                        //  dispatch(setWidthFilter(e))
                        //  console.log(val)

                        filterProducts &&
                          setFilterProducts(
                            topic.section.filter((item) => item.width === val)
                          ),
                        setSelected(e.target.value)
                      )
                      /*  topic.section &&
    Array.from(
      new Set(topic.section.map((item: any) => item.width))
    ).map((val) => console.log(typeof val));
                      */
                    }
                  />
                  <label htmlFor={val}>{val}</label>
                </div>
              ))}
            <a
              className="filter_clear"
              onClick={() => (
                setFilterProducts(topic.section), setSelected("")
              )}
            >
              Очистить
            </a>
          </div>
          <div className="col-12 col-sm-12 col-md-12 col-lg-9 col-xl-9">
            <div className="row">
              {filterProducts &&
                filterProducts.map((item) => (
                  <ProductCard item={item} topic={topic} key={item.id} />
                ))}
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default CatalogSectionPage;
