import React from "react";
import { useParams, Link } from "react-router-dom";
import Breadcrums from "../components/Breadcrumbs";
import { CatalogList } from "../utils/constants";
import ProductCard from "../components/ProductCard";

const CatalogSectionPage = () => {
  const params = useParams();
  const topic = CatalogList.filter((item) => item.slug === params.slug)[0];
  function filterByProp(arr: any, prop: string) {
    const arr_prop = arr.map((item: any) => item.prop);
    console.log(arr_prop, prop);
  }

  console.log(filterByProp(topic.section, "width"));

  return (
    <>
      <Breadcrums title={topic.title} />
      <div className="container inner_content">
        <div className="row">
          <div className="col-12 col-sm-12 col-md-12 col-lg-3 col-xl-3">
            <ul className="leftmenu">
              {CatalogList.map((item) => (
                <li
                  className={item.slug === params.slug ? "active" : ""}
                  key={item.slug}
                >
                  <Link to={`/catalog/${item.slug}`}>{item.title}</Link>
                </li>
              ))}
            </ul>
            {topic.section &&
              topic.section.map((item) => <div>{item.width}</div>)}
          </div>
          <div className="col-12 col-sm-12 col-md-12 col-lg-9 col-xl-9">
            <div className="row">
              {topic.section &&
                topic.section.map((item) => (
                  <ProductCard item={item} topic={topic} key={item.id} />
                ))}
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default CatalogSectionPage;
