import React, { useState } from "react";
import { Accordion } from "react-accordion-ts";
import "react-accordion-ts/src/panel.css";
import { useForm } from "react-hook-form";
import Breadcrums from "../components/Breadcrumbs";
import { VacancyList } from "../utils/constants";

const VacancyPage = () => {
  const items = [
    {
      title: "Accordion Item #1",
      content: (
        <div>
          <strong>This is the first item's accordion body.</strong> It is hidden
          by default, but shown when title is clicked. It will also be hidden if
          the title is clicked again or when another item is clicked. You can
          pass HTML tags in the content such as <u>underline tag</u>,{" "}
          <i>italic</i>, or even another list like this:
          <ul>
            <li>Bread</li>
            <li>Eggs</li>
            <li>Milk</li>
          </ul>
        </div>
      ),
    },
    {
      title: "Accordion Item #2",
      content: (
        <div>
          <strong>This is the second item's accordion body.</strong> It is
          hidden by default, but shown when title is clicked. It will also be
          hidden if the title is clicked again or when another item is clicked.
          You can pass HTML tags in the content such as <u>underline tag</u>,{" "}
          <i>italic</i>, or even another list like this:
          <ul>
            <li>Bread</li>
            <li>Eggs</li>
            <li>Milk</li>
          </ul>
        </div>
      ),
    },
    {
      title: "Accordion Item #3",
      content: (
        <div>
          <strong>This is the third item's accordion body.</strong> It is hidden
          by default, but shown when title is clicked. It will also be hidden if
          the title is clicked again or when another item is clicked. You can
          pass HTML tags in the content such as <u>underline tag</u>,{" "}
          <i>italic</i>, or even another list like this:
          <ul>
            <li>Bread</li>
            <li>Eggs</li>
            <li>Milk</li>
          </ul>
        </div>
      ),
    },
  ];
  const [show, setShow] = useState(false);
  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
  } = useForm();
  const validateOptions = {
    name: { required: "Это поле необходимо заполнить" },
    question: { required: "Это поле необходимо заполнить" },
  };
  const onSubmit = () => {
    setShow(true);
    reset();
  };
  return (
    <>
      <Breadcrums />
      <div className="container">
        <div className="row">
          <div className="col-12 col-sm-12 col-md-12 col-lg-7 col-xl-7">
            <div className="answer_wrap">
              <Accordion items={items} duration={300} multiple={false} />
            </div>
          </div>
          <div className="col-12 col-sm-12 col-md-12 col-lg-1 col-xl-1"></div>
          <div className="col-12 col-sm-12 col-md-12 col-lg-4 col-xl-4 content_form">
            <div className="form_wrapper">
              <div className="form_title">Задайте вопрос</div>
              {!show && (
                <form onSubmit={handleSubmit(onSubmit)}>
                  <>
                    <input
                      type="text"
                      placeholder="Ваше имя"
                      className={errors.name ? "wrong-field" : ""}
                      {...register("name", validateOptions.name)}
                    />
                    {errors.name && (
                      <p className="error">
                        <>{errors.name.message}</>
                      </p>
                    )}
                    <textarea
                      placeholder="Ваш вопрос"
                      className={errors.question ? "wrong-field" : ""}
                      {...register("question", validateOptions.question)}
                    ></textarea>
                    {errors.question && (
                      <p className="error">
                        <>{errors.question.message}</>
                      </p>
                    )}
                    <input type="submit" value="Отправить" />
                  </>
                </form>
              )}
              {show && (
                <div className="mail-status">
                  Ваше сообщение успешно отправлено!
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default VacancyPage;
