import { SET_WIDTH_FILTER } from "./actionTypes";
import { setWidthFilter } from "./actions";
import { FiltersState } from "./types";

const initialState: FiltersState = {
  filters: [],
};

type FilterAction = ReturnType<typeof setWidthFilter>;

export default function filterReducer(
  state = initialState,
  action: FilterAction
) {
  switch (action.type) {
    case SET_WIDTH_FILTER:
      let { name, value, checked } = action.payload;
      return {
        ...state,
        filters: state.filter((item) => item.width === value),
      };
    default:
      return state;
  }
}
