import React from "react";
import { useParams } from "react-router-dom";
import { useDispatch } from "react-redux";
import Breadcrums from "../components/Breadcrumbs";
import { CatalogList } from "../utils/constants";
import noimage from "../assets/images/noimage.png";
import { addToCart } from "../redux/reducer";

const CatalogSectionPage = () => {
  const params = useParams();
  let dispatch = useDispatch();
  const topic = CatalogList.filter((item) => item.slug === params.slug)[0];
  return (
    <>
      <Breadcrums title={topic.title} />
      <div className="container">
        <div className="row">
          <div className="col-12 col-sm-12 col-md-12 col-lg-3 col-xl-3"></div>
          <div className="col-12 col-sm-12 col-md-12 col-lg-9 col-xl-9">
            <div className="row">
              {topic.section &&
                topic.section.map((item) => (
                  <div
                    className="col-12 col-sm-12 col-md-4 col-lg-4 col-xl-4"
                    key={item.id}
                  >
                    <div className="tovar text-center shk-item">
                      <a
                        className="razdel_cataloga_img"
                        href={`/catalog/${topic.slug}/${item.slug}`}
                      >
                        <img
                          className="shk-image"
                          src={item.image ? `/images/${item.image}` : noimage}
                          alt={item.title}
                        />
                      </a>
                      <div className="tovar_name">
                        <a href={`/catalog/${topic.slug}/${item.slug}`}>
                          {item.title}
                        </a>
                      </div>

                      <div className="tovar_price">
                        <span className="shk-price">{item.price}</span> byn
                      </div>
                      <button
                        onClick={() => dispatch(addToCart(item))}
                        className="shk-but to_cart"
                      >
                        <span className="to_cart_span">В корзину</span>
                      </button>
                    </div>
                  </div>
                ))}
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default CatalogSectionPage;
