import React from "react";
import { useParams } from "react-router-dom";
import Breadcrums from "../components/Breadcrumbs";
import { CatalogList } from "../utils/constants";

const ProductPage = () => {
  const params = useParams();
  //const topic = CatalogList.filter((item) => item.slug === params.slug)[0];
  const topic = CatalogList.map(
    (item) => item.section && item.section.filter((i) => i.slug === params.slug)
  )[0][0];
  console.log(topic);

  return (
    <>
      {topic && <Breadcrums title={topic.title} />}
      <div className="container inner_content">
        <div className="row">
          <div className="col-12 col-sm-12 col-md-12 col-lg-3 col-xl-3"></div>
          <div className="col-12 col-sm-12 col-md-12 col-lg-9 col-xl-9">
            <div className="row"></div>
          </div>
        </div>
      </div>
    </>
  );
};

export default ProductPage;
