import { Dispatch, AnyAction } from "redux";

function typedAction(type: string, payload?: any) {
  return { type, payload };
}

type Product = {
  id: number;
  title: string;
  price: number;
  image: string;
};

type CartItem = {
  id: number;
  title: string;
  quantity: number;
  price: number;
  image: string;
};

export type ProductState = {
  products: Product[];
  loading: boolean;
  cart: CartItem[];
};

const initialState: ProductState = {
  products: [],
  loading: false,
  cart: [],
};

const addProducts = (products: Product[]) => {
  return typedAction("products/ADD_PRODUCTS", products);
};

export const addToCart = (product: Product, quantity: number) => {
  return typedAction("products/ADD_TO_CART", {
    product,
    quantity,
  });
};

type ProductAction = ReturnType<typeof addProducts | typeof addToCart>;

export default function reducer(
  state = initialState,
  action: ProductAction
): ProductState {
  switch (action.type) {
    case "products/ADD_PRODUCTS":
      return {
        ...state,
        products: [...state.products, ...action.payload],
      };
    case "products/ADD_TO_CART":
      return {
        ...state,
        cart: [
          ...state.cart,
          {
            id: action.payload.product.id,
            title: action.payload.product.title,
            quantity: action.payload.quantity,
            price: action.payload.price,
            image: action.payload.image,
          },
        ],
      };
    default:
      return state;
  }
}
