import React from "react";
import Slider from "react-slick";
import { ReviewsList, VideoList } from "../utils/constants";
import Breadcrums from "../components/Breadcrumbs";
import Fancybox from "../components/Fancybox";

const ReviewsPage = () => {
  const settings = {
    arrows: true,
    dots: true,
    infinite: false,
    slidesToShow: 4,
    slidesToScroll: 1,
    speed: 300,
    cssEase: "cubic-bezier(0.77, 0, 0.175, 1)",
    responsive: [
      {
        breakpoint: 1401,
        settings: {
          arrows: false,
        },
      },
      {
        breakpoint: 1200,
        settings: {
          slidesToShow: 3,
          arrows: false,
        },
      },
      {
        breakpoint: 992,
        settings: {
          slidesToShow: 2,
          arrows: false,
        },
      },
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 1,
          arrows: false,
        },
      },
    ],
  };
  return (
    <>
      <Breadcrums />
      <div className="container reviews_wrapper text-center">
        <div id="reviews">
          <Fancybox>
            <Slider {...settings}>
              {ReviewsList.map(
                (review) =>
                  review.image && (
                    <div className="item" key={review.id}>
                      <a
                        className="fancybox_img"
                        data-fancybox="gallery"
                        href={`/images/${review.image}`}
                      >
                        <span className="review_img">
                          <span className="review_overlay"></span>
                          <img src={`images/${review.image}`} alt="Отзывы" />
                        </span>
                      </a>
                    </div>
                  )
              )}
            </Slider>
          </Fancybox>
        </div>
      </div>
      <div className="container video_reviews text-center">
        <div className="row">
          {VideoList.map((video) => (
            <div className="col-12 col-sm-12 col-md-4 col-lg-4 col-xl-4 video_col">
              <a
                href="https://www.youtube.com/watch?v=W3xfn-xb-GE&amp;autoplay=0"
                data-fancybox
                key={video.id}
              >
                <span className="video_img">
                  {video.image && (
                    <img src={`images/${video.image}`} alt="Видео" />
                  )}
                </span>
              </a>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default ReviewsPage;
