import React from "react";
import useBreadcrumbs from "use-react-router-breadcrumbs";
import { NavLink } from "react-router-dom";
import { GalleryList } from "../utils/constants";

const DynamicGalleryBreadcrumb = ({ match }) => (
  <span>{match.params.slug}</span>
);
const routes = [
  { path: "/", breadcrumb: "Главная" },
  { path: "/news", breadcrumb: "Новости" },
  { path: "/news/:slug", breadcrumb: "" },
  { path: "/gallery", breadcrumb: "Галерея" },
  {
    path: "/gallery/:slug",
    breadcrumb: DynamicGalleryBreadcrumb,
  },
  { path: "/contacts", breadcrumb: "Контакты" },
  { path: "/*", breadcrumb: "404" },
];

const Breadcrums = () => {
  const breadcrumbs = useBreadcrumbs(routes);

  return (
    <>
      <ul className="B_crumbBox">
        {breadcrumbs.map(({ match, breadcrumb }) => (
          <li class="breadcrumb-item">
            <NavLink key={match.pathname} to={match.pathname}>
              {breadcrumb}
            </NavLink>
          </li>
        ))}
      </ul>
    </>
  );
};

export default Breadcrums;
