import React from "react";
import Breadcrums from "../components/Breadcrumbs";
import Slider from "react-slick";
import { ReviewsList } from "../utils/constants";

const ReviewsPage = () => {
  const settings = {
    arrows: true,
    dots: true,
    infinite: true,
    slidesToShow: 6,
    slidesToScroll: 1,
    speed: 500,
    autoplay: true,
    autoplaySpeed: 4000,
    responsive: [
      {
        breakpoint: 992,
        settings: {
          slidesToShow: 3,
        },
      },
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 1,
          arrows: false,
          dots: true,
        },
      },
    ],
  };
  return (
    <>
      <Breadcrums />
      <div className="container reviews_wrapper text-center">
        <div id="reviews">
          <Slider {...settings}>
            {ReviewsList.map(
              (review) =>
                review.image && (
                  <div className="item" key={review.id}>
                    <div className="clients_block text-center">
                      <img src={`images/${review.image}`} alt="Отзывы" />
                    </div>
                  </div>
                )
            )}
          </Slider>
        </div>
      </div>
    </>
  );
};

export default ReviewsPage;
