import React, { useState } from "react";
import Modal from "react-bootstrap/Modal";
import { useForm } from "react-hook-form";
import InputMask from "react-input-mask";

interface Props {
  btnClass?: string;
  modalBtnText?: string;
}

const ModalForm = ({ btnClass, modalBtnText }: Props) => {
  const [showModal, setShowModal] = useState(false);
  const handleClose = () => setShowModal(false);
  const handleShow = () => setShowModal(true);
  const [show, setShow] = useState(false);
  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
  } = useForm();
  const validateOptions = {
    name: { required: "Это поле необходимо заполнить" },
    phone: {
      required: "Это поле необходимо заполнить",
      minLength: {
        value: 9,
        message: "В номере должно быть не менее 9 цифр",
      },
    },
  };
  const onSubmit = () => {
    setShow(true);
    reset();
  };
  return (
    <>
      <span className={btnClass} onClick={handleShow}>
        {modalBtnText}
      </span>
      <Modal show={showModal} onHide={handleClose}>
        <div className="form_wrapper">
          <div className="form_title">Консультация</div>
          <div className="form_text">
            У вас остались сомнения или возникли какие-нибудь вопросы?
          </div>
          {!show && (
            <form onSubmit={handleSubmit(onSubmit)}>
              <>
                <input
                  type="text"
                  placeholder="Имя"
                  className={errors.name ? "wrong-field" : ""}
                  {...register("name", validateOptions.name)}
                />
                {errors.name && (
                  <p className="error">
                    <>{errors.name.message}</>
                  </p>
                )}
                <InputMask
                  mask="+375 (99) 999-99-99"
                  type="text"
                  placeholder="Номер телефона"
                  className={errors.phone ? "wrong-field" : ""}
                  {...register("phone", validateOptions.phone)}
                />
                {errors.phone && (
                  <p className="error">
                    <>{errors.phone.message}</>
                  </p>
                )}
                <textarea
                  placeholder="Сообщение"
                  {...register("message")}
                ></textarea>
                <input type="submit" value="Отправить" />
              </>
            </form>
          )}
          {show && (
            <div className="mail-status">
              Ваше сообщение успешно отправлено!
            </div>
          )}
        </div>
      </Modal>
    </>
  );
};

export default ModalForm;
