import React, { useState } from "react";
import { Link } from "react-router-dom";
import ReactPaginate from "react-paginate";
import { SaleList } from "../utils/constants";
import Breadcrums from "../components/Breadcrumbs";

const PER_PAGE = 9;

const SalePage = () => {
  const [currentPage, setCurrentPage] = useState(0);
  const offset = currentPage * PER_PAGE;
  const pageCount = Math.ceil(SaleList.length / PER_PAGE);
  function handlePageClick(selectedPage: any) {
    setCurrentPage(selectedPage.selected);
  }
  const currentPageData = SaleList.slice(offset, offset + PER_PAGE).map(
    (sale) => (
      <div
        className="col-12 col-sm-12 col-md-4 col-lg-4 col-xl-4 gallery_razdel_col"
        key={sale.id}
      >
        <Link to={`/sale/${gallery.slug}`}>
          <span className="sale_image">
            {sale.image && (
              <img src={`/images/${sale.image}`} alt={sale.title} />
            )}
          </span>
          <span className="news_a">{sale.title}</span>
        </Link>
      </div>
    )
  );
  return (
    <>
      <Breadcrums />
      <div className="container">
        <div className="row">{currentPageData}</div>
        <ReactPaginate
          previousLabel={"Назад"}
          nextLabel={"Вперед"}
          pageCount={pageCount}
          onPageChange={handlePageClick}
          containerClassName={"pagination"}
          pageClassName={"page-item"}
          pageLinkClassName={"page-link"}
          previousClassName={"page-item"}
          nextClassName={"page-item"}
          previousLinkClassName={"page-link"}
          nextLinkClassName={"page-link"}
          disabledClassName={"disabled"}
          activeClassName={"active"}
        />
      </div>
    </>
  );
};

export default SalePage;
