import React from "react";
import { Link } from "react-router-dom";

interface MenuItem {
  text: string;
  path: string;
  children?: { text: string; path: string }[];
}

const MenuItem = ({ text, path, children }: MenuItem) => {
  return (
    <li className="level1" key={path}>
      <Link to={path}>
        {text}
        {text === "Каталог" && (
          <>
            <span className="open_top_menu">
              <svg
                width="9"
                height="4"
                viewBox="0 0 9 4"
                fill="none"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path d="M4.5 4L8.39711 0.25H0.602886L4.5 4Z" fill="white" />
              </svg>
            </span>
            <span className="open_catalog">
              <span></span>
              <span></span>
              <span></span>
            </span>
            <span className="mobile_arrow">
              <svg
                width="9"
                height="4"
                viewBox="0 0 9 4"
                fill="none"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path d="M4.5 4L8.39711 0.25H0.602886L4.5 4Z" fill="#151515" />
              </svg>
            </span>
          </>
        )}
      </Link>
      {children && (
        <ul>
          {children.map((child: any) => (
            <li className="level2" key={child.path}>
              <Link to={child.path}>{child.text}</Link>
            </li>
          ))}
        </ul>
      )}
    </li>
  );
};

export default MenuItem;
