import React from "react";
import { useParams } from "react-router-dom";
import Slider from "react-slick";
import Breadcrums from "../components/Breadcrumbs";
import { CatalogList } from "../utils/constants";

const ProductPage = () => {
  const params = useParams();
  const topic = CatalogList.map(
    (item) => item.section && item.section.filter((i) => i.slug === params.slug)
  )[0];
  const settings = {
    arrows: false,
    dots: true,
    infinite: true,
    slidesToShow: 1,
    slidesToScroll: 1,
    speed: 300,
    cssEase: "cubic-bezier(0.77, 0, 0.175, 1)",
  };

  return (
    <>
      {topic && (
        <>
          {topic[0] && <Breadcrums title={topic[0].title} />}
          <div className="container inner_content">
            <div className="row">
              <div className="col-12 col-sm-12 col-md-12 col-lg-8 col-xl-8">
                {topic[0].image && (
                  <img src={`/images/${topic[0].image}`} alt={topic[0].title} />
                )}
                {topic[0].gallery && (
                  <img src={`/images/${topic[0].image}`} alt={topic[0].title} />
                )}
              </div>
              <div className="col-12 col-sm-12 col-md-12 col-lg-4 col-xl-4"></div>
            </div>
          </div>
        </>
      )}
    </>
  );
};

export default ProductPage;
