import {
  ADD_TO_CART,
  REMOVE_FROM_CART,
  INCREMENT_QUANTITY,
  DECREMENT_QUANTITY,
  CLEAR_CART,
  CLEAR_CARTORDER,
} from "./actionTypes";
import { typedAction } from "./type";

export type Product = {
  id: number;
  title: string;
  price: number;
  slug?: string;
  image?: string;
  new?: string;
  recommend?: string;
  popular?: string;
};

export type CartItem = {
  id: number;
  title: string;
  quantity: number;
  price: number;
  image?: string;
};

export interface CartState {
  cart: CartItem[];
}

const initialState: CartState = {
  cart: [],
};

export const addToCart = (product: Product) => {
  return typedAction(ADD_TO_CART, product);
};

export const removeFromCart = (product: Product) => {
  return typedAction(REMOVE_FROM_CART, product);
};

export const incrementQuantity = (product: Product) => {
  return typedAction(INCREMENT_QUANTITY, product);
};

export const decrementQuantity = (product: Product) => {
  return typedAction(DECREMENT_QUANTITY, product);
};

export const clearCart = () => {
  return typedAction(CLEAR_CART);
};

export const clearCartOrder = () => {
  return typedAction(CLEAR_CARTORDER);
};

type ProductAction = ReturnType<
  | typeof addToCart
  | typeof removeFromCart
  | typeof incrementQuantity
  | typeof decrementQuantity
  | typeof clearCart
  | typeof clearCartOrder
>;

export default function cartReducer(
  state = initialState,
  action: ProductAction
) {
  switch (action.type) {
    case ADD_TO_CART:
      const itemInCart = state.cart.find(
        (item) => item.id === action.payload.id
      );
      if (itemInCart) {
        const tempCart = state.cart.map((cartItem) => {
          if (cartItem.id === action.payload.id) {
            return { ...cartItem, quantity: cartItem.quantity + 1 };
          } else {
            return cartItem;
          }
        });
        return { ...state, cart: tempCart };
      } else {
        return {
          ...state,
          cart: [...state.cart, { ...action.payload, quantity: 1 }],
        };

        // localStorage.setItem('token', payload);
      }

    case REMOVE_FROM_CART:
      if (window.confirm("Удалить товар?")) {
        return {
          ...state,
          cart: [...state.cart.filter((item) => item.id !== action.payload.id)],
        };
      } else {
        return state;
      }
    case INCREMENT_QUANTITY:
      const item = state.cart.find((item) => item.id === action.payload.id);
      if (item) {
        item.quantity++;
        return {
          ...state,
          cart: [...state.cart],
        };
      }
      return state;
    case DECREMENT_QUANTITY:
      const item_decr = state.cart.find(
        (item) => item.id === action.payload.id
      );
      if (item_decr) {
        if (item_decr.quantity == 1) {
          item_decr.quantity = 1;
        } else {
          item_decr.quantity--;
        }
        return {
          ...state,
          cart: [...state.cart],
        };
      }
      return state;
    case CLEAR_CART:
      if (window.confirm("Очистить корзину?")) {
        // localStorage.removeItem('cart');
        return { ...state, cart: [] };
      } else {
        return state;
      }
    case CLEAR_CARTORDER:
      return { ...state, cart: [] };
    default:
      return state;
  }
}
