const intitialState = {
  items: {
    allItem: [],
    loading: true,
  },
  cart: {
    cartItems: [],
  },
};

const reducer = (state = intitialState, action) => {
  switch (action.type) {
    case "ITEM_LOADED":
      return {
        ...state,
        items: {
          allItem: action.payload,
          loading: false,
        },
      };
    case "ITEMS_LOADING":
      return {
        ...state,
        items: {
          loading: true,
        },
      };

    default:
      return state;
  }
};
export default reducer;
