import React, { FormEvent, useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import { useDispatch } from "react-redux";
import Breadcrums from "../components/Breadcrumbs";
import { CatalogList } from "../utils/constants";
import ProductCard from "../components/ProductCard";
import { setWidthFilter, getSectionProducts } from "../redux/actions";

const CatalogSectionPage = () => {
  const params = useParams();
  const topic = CatalogList.filter((item) => item.slug === params.slug)[0];
  const dispatch = useDispatch();
  const [filterProducts, setFilterProducts] = useState({});
  const [selected, setSelected] = useState<String>();
  useEffect(() => {
    try {
      dispatch(dispatch(getSectionProducts(topic.section)));
    } catch (error) {
      console.log(error);
    }
  }, []);
  return (
    <>
      <Breadcrums title={topic.title} />
      <div className="container inner_content">
        <div className="row">
          <div className="col-12 col-sm-12 col-md-12 col-lg-3 col-xl-3">
            <ul className="leftmenu">
              {CatalogList.map((item) => (
                <li
                  className={item.slug === params.slug ? "active" : ""}
                  key={item.slug}
                >
                  <Link to={`/catalog/${item.slug}`}>{item.title}</Link>
                </li>
              ))}
            </ul>
            <div className="filter_btn">Фильтр</div>
            <div id="filters">
              <div className="filter_block">
                <div className="filter_head">
                  <h3>Ширина, см</h3>
                </div>
                {topic.section &&
                  Array.from(
                    new Set(topic.section.map((item: any) => item.width))
                  ).map((val) => (
                    <div className="filter_row" key={val}>
                      <input
                        type="radio"
                        id={val}
                        name="width"
                        value={val}
                        checked={selected === val.toString()}
                        onChange={(e: React.ChangeEvent<HTMLInputElement>) => (
                          filterProducts &&
                            setFilterProducts(
                              topic.section.filter((item) => item.width === val)
                            ),
                          setSelected(e.target.value)
                        )}
                      />
                      <label htmlFor={val}>{val}</label>
                    </div>
                  ))}
              </div>
              {/*    <a
                className="filter_clear"
                onClick={() => (
                  setFilterProducts(topic.section), setSelected("")
                )}
              >
                Очистить
                </a>*/}
            </div>
          </div>
          <div className="col-12 col-sm-12 col-md-12 col-lg-9 col-xl-9">
            <div className="row">
              {filterProducts &&
                Array.from(filterProducts).map((item) => (
                  <ProductCard item={item} topic={topic} key={item.id} />
                ))}
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default CatalogSectionPage;
